mapper.select:
select BIZ_DATE,TRADE_ID,TRADE_DATE,BOND_CODE,BOND_NAME,BOND_TYPE,BOND_FACE_AMT,SETTLE_DATE,SETTLE_TYPE,SETTLE_AMT,BUYER_MEM_CODE,BUYER_MEM_NAME,BUYER_HOLDER_ACCOUNT,BUYER_HOLDER_ACCOUNT_NAME,SELLER_MEM_CODE,SELLER_MEM_NAME,SELLER_HOLDER_ACCOUNT,SELLER_HOLDER_ACCOUNT_NAME,SETTLE_ORDER_ID,BOND_SETTLE_ID,CASH_SETTLE_ID,BOND_SETTLE_STATUS,BOND_SETTLE_STATUS_UPDATE_TM,CASH_SETTLE_STATUS,CASH_SETTLE_STATUS_UPDATE_TM,SETTLE_ORDER_STATUS,SETTLE_ORDER_STATUS_UPDATE_TM,CASH_PEROID,BOND_PEROID,INPUT_OPER_ID,INPUT_TM,REVIEW_OPER_ID,REVIEW_TM,CONFIRM_OPER_ID,CONFIRM_TM,SRC_FROM,REMARK from SETTLE_ORDER

    
        
             AND BIZ_DATE like #{condition.bizDate,jdbcType=VARCHAR }
             AND BIZ_DATE = #{condition.bizDate,jdbcType=VARCHAR}
        
    
    
        
             AND TRADE_ID like #{condition.tradeId,jdbcType=VARCHAR }
             AND TRADE_ID = #{condition.tradeId,jdbcType=VARCHAR}
        
    
    
        
             AND TRADE_DATE like #{condition.tradeDate,jdbcType=VARCHAR }
             AND TRADE_DATE = #{condition.tradeDate,jdbcType=VARCHAR}
        
    
    
        
             AND BOND_CODE like #{condition.bondCode,jdbcType=VARCHAR }
             AND BOND_CODE = #{condition.bondCode,jdbcType=VARCHAR}
        
    
    
        
             AND BOND_NAME like #{condition.bondName,jdbcType=VARCHAR }
             AND BOND_NAME = #{condition.bondName,jdbcType=VARCHAR}
        
    
    
        
             AND BOND_TYPE like #{condition.bondType,jdbcType=VARCHAR }
             AND BOND_TYPE = #{condition.bondType,jdbcType=VARCHAR}
        
    
    
        
             AND BOND_FACE_AMT like #{condition.bondFaceAmt,jdbcType=NUMERIC }
             AND BOND_FACE_AMT = #{condition.bondFaceAmt,jdbcType=NUMERIC}
        
    
    
        
             AND SETTLE_DATE like #{condition.settleDate,jdbcType=VARCHAR }
             AND SETTLE_DATE = #{condition.settleDate,jdbcType=VARCHAR}
        
    
    
        
             AND SETTLE_TYPE like #{condition.settleType,jdbcType=VARCHAR }
             AND SETTLE_TYPE = #{condition.settleType,jdbcType=VARCHAR}
        
    
    
        
             AND SETTLE_AMT like #{condition.settleAmt,jdbcType=VARCHAR }
             AND SETTLE_AMT = #{condition.settleAmt,jdbcType=VARCHAR}
        
    
    
        
             AND BUYER_MEM_CODE like #{condition.buyerMemCode,jdbcType=VARCHAR }
             AND BUYER_MEM_CODE = #{condition.buyerMemCode,jdbcType=VARCHAR}
        
    
    
        
             AND BUYER_MEM_NAME like #{condition.buyerMemName,jdbcType=VARCHAR }
             AND BUYER_MEM_NAME = #{condition.buyerMemName,jdbcType=VARCHAR}
        
    
    
        
             AND BUYER_HOLDER_ACCOUNT like #{condition.buyerHolderAccount,jdbcType=VARCHAR }
             AND BUYER_HOLDER_ACCOUNT = #{condition.buyerHolderAccount,jdbcType=VARCHAR}
        
    
    
        
             AND BUYER_HOLDER_ACCOUNT_NAME like #{condition.buyerHolderAccountName,jdbcType=VARCHAR }
             AND BUYER_HOLDER_ACCOUNT_NAME = #{condition.buyerHolderAccountName,jdbcType=VARCHAR}
        
    
    
        
             AND SELLER_MEM_CODE like #{condition.sellerMemCode,jdbcType=VARCHAR }
             AND SELLER_MEM_CODE = #{condition.sellerMemCode,jdbcType=VARCHAR}
        
    
    
        
             AND SELLER_MEM_NAME like #{condition.sellerMemName,jdbcType=VARCHAR }
             AND SELLER_MEM_NAME = #{condition.sellerMemName,jdbcType=VARCHAR}
        
    
    
        
             AND SELLER_HOLDER_ACCOUNT like #{condition.sellerHolderAccount,jdbcType=VARCHAR }
             AND SELLER_HOLDER_ACCOUNT = #{condition.sellerHolderAccount,jdbcType=VARCHAR}
        
    
    
        
             AND SELLER_HOLDER_ACCOUNT_NAME like #{condition.sellerHolderAccountName,jdbcType=VARCHAR }
             AND SELLER_HOLDER_ACCOUNT_NAME = #{condition.sellerHolderAccountName,jdbcType=VARCHAR}
        
    
    
        
             AND SETTLE_ORDER_ID like #{condition.settleOrderId,jdbcType=VARCHAR }
             AND SETTLE_ORDER_ID = #{condition.settleOrderId,jdbcType=VARCHAR}
        
    
    
        
             AND BOND_SETTLE_ID like #{condition.bondSettleId,jdbcType=VARCHAR }
             AND BOND_SETTLE_ID = #{condition.bondSettleId,jdbcType=VARCHAR}
        
    
    
        
             AND CASH_SETTLE_ID like #{condition.cashSettleId,jdbcType=VARCHAR }
             AND CASH_SETTLE_ID = #{condition.cashSettleId,jdbcType=VARCHAR}
        
    
    
        
             AND BOND_SETTLE_STATUS like #{condition.bondSettleStatus,jdbcType=VARCHAR }
             AND BOND_SETTLE_STATUS = #{condition.bondSettleStatus,jdbcType=VARCHAR}
        
    
    
        
             AND BOND_SETTLE_STATUS_UPDATE_TM like #{condition.bondSettleStatusUpdateTm,jdbcType=VARCHAR }
             AND BOND_SETTLE_STATUS_UPDATE_TM = #{condition.bondSettleStatusUpdateTm,jdbcType=VARCHAR}
        
    
    
        
             AND CASH_SETTLE_STATUS like #{condition.cashSettleStatus,jdbcType=VARCHAR }
             AND CASH_SETTLE_STATUS = #{condition.cashSettleStatus,jdbcType=VARCHAR}
        
    
    
        
             AND CASH_SETTLE_STATUS_UPDATE_TM like #{condition.cashSettleStatusUpdateTm,jdbcType=VARCHAR }
             AND CASH_SETTLE_STATUS_UPDATE_TM = #{condition.cashSettleStatusUpdateTm,jdbcType=VARCHAR}
        
    
    
        
             AND SETTLE_ORDER_STATUS like #{condition.settleOrderStatus,jdbcType=VARCHAR }
             AND SETTLE_ORDER_STATUS = #{condition.settleOrderStatus,jdbcType=VARCHAR}
        
    
    
        
             AND SETTLE_ORDER_STATUS_UPDATE_TM like #{condition.settleOrderStatusUpdateTm,jdbcType=VARCHAR }
             AND SETTLE_ORDER_STATUS_UPDATE_TM = #{condition.settleOrderStatusUpdateTm,jdbcType=VARCHAR}
        
    
    
        
             AND CASH_PEROID like #{condition.cashPeroid,jdbcType=VARCHAR }
             AND CASH_PEROID = #{condition.cashPeroid,jdbcType=VARCHAR}
        
    
    
        
             AND BOND_PEROID like #{condition.bondPeroid,jdbcType=VARCHAR }
             AND BOND_PEROID = #{condition.bondPeroid,jdbcType=VARCHAR}
        
    
    
        
             AND INPUT_OPER_ID like #{condition.inputOperId,jdbcType=VARCHAR }
             AND INPUT_OPER_ID = #{condition.inputOperId,jdbcType=VARCHAR}
        
    
    
        
             AND INPUT_TM like #{condition.inputTm,jdbcType=VARCHAR }
             AND INPUT_TM = #{condition.inputTm,jdbcType=VARCHAR}
        
    
    
        
             AND REVIEW_OPER_ID like #{condition.reviewOperId,jdbcType=VARCHAR }
             AND REVIEW_OPER_ID = #{condition.reviewOperId,jdbcType=VARCHAR}
        
    
    
        
             AND REVIEW_TM like #{condition.reviewTm,jdbcType=VARCHAR }
             AND REVIEW_TM = #{condition.reviewTm,jdbcType=VARCHAR}
        
    
    
        
             AND CONFIRM_OPER_ID like #{condition.confirmOperId,jdbcType=VARCHAR }
             AND CONFIRM_OPER_ID = #{condition.confirmOperId,jdbcType=VARCHAR}
        
    
    
        
             AND CONFIRM_TM like #{condition.confirmTm,jdbcType=VARCHAR }
             AND CONFIRM_TM = #{condition.confirmTm,jdbcType=VARCHAR}
        
    
    
        
             AND SRC_FROM like #{condition.srcFrom,jdbcType=VARCHAR }
             AND SRC_FROM = #{condition.srcFrom,jdbcType=VARCHAR}
        
    
    
        
             AND REMARK like #{condition.remark,jdbcType=VARCHAR }
             AND REMARK = #{condition.remark,jdbcType=VARCHAR}
        
    
    ${dataAuth}

 order by ${sort}

mapper.delete:
delete from SETTLE_ORDER where  SETTLE_ORDER_ID in

  #{settleOrderId,jdbcType=VARCHAR}

${dataAuth}
