where MEM_CODE = #{memCode,jdbcType=VARCHAR}
  and HOLDER_ACCOUNT = #{holderAccount,jdbcType=VARCHAR}
  and TITLE_CODE = #{titleCode,jdbcType=VARCHAR}
  and CASH_ACCOUNT = #{cashAccount,jdbcType=VARCHAR}


